SELECT t.QTBLJLXH as yjlxh, b.HISSYXH as jzlsh, c.PATID as patid, b.ZYHM as zyh, b.HZXM as hzxm, b.SFZH as sfzhm, b.BRXB as xbdm, ( SELECT CASE b.BRXB WHEN '2' THEN '女' WHEN '1' THEN '男' ELSE '其它' END ) AS xbmc, ISNULL(convert (varchar,(YEAR(GETDATE())-YEAR(convert(datetime, b.CSRQ)))) ,'NA') as nls, datediff(month,b.CSRQ,substring(convert(char(8),getdate(),112),1,8)) %12 as nly, b.MZDM as mz, d.NAME as mzmc, b.HYZK as hyzkdm, ( SELECT CASE b.HYZK WHEN '0' THEN '未婚' WHEN '1' THEN '已婚' WHEN '2' THEN '离独' WHEN '3' THEN '丧偶' ELSE '未知 ' END ) AS hyzkmc, ISNULL((SELECT e.NAME FROM EMR_SYS_DQDMK e WHERE b.SSDM = e.DQDM),'NA') as dzsf, ISNULL((SELECT e.NAME FROM EMR_SYS_DQDMK e WHERE b.QXDM = e.DQDM),'NA') as dzsq, ISNULL((SELECT e.NAME FROM EMR_SYS_DQDMK e WHERE b.QXDM = e.DQDM),'NA') as dzx, ISNULL((SELECT e.NAME FROM EMR_SYS_DQDMK e WHERE b.QXDM = e.DQDM),'NA') as dzxz, 'NA' as dzc, 'NA' as dzmphm, b.ZYDM as zylbdm, i.NAME as zylbmc,
CASE WHEN b.CYRQ IS NULL THEN GETDATE() ELSE b.CYRQ END as cyrq, t.SYXH as syxh, t.TJZT as tjzt, GETDATE() as gxsj, t.YXJL as yxjl FROM EMR_QTBLJLK t LEFT JOIN CPOE_BRSYK c ON t.SYXH = c.EMRXH LEFT JOIN EMR_BRSYK b ON b.SYXH = t.SYXH LEFT JOIN PUB_MZDMK d on b.MZDM = d.ID LEFT JOIN EMR_SYS_ZDFLMXK i on b.ZYDM=i.MXDM and LBDM = 41
AND t.QTBLJLXH = #{yjlxh , jdbcType= VARCHAR}

SELECT t.QTBLJLXH as yjlxh, b.HISSYXH as jzlsh, c.PATID as patid, b.ZYHM as zyh, c.KSDM as ksdm, c.KSMC as ksmc, c.BQDM as bqdm, c.BQMC as bqmc, ISNULL(a.fjh,'NA') as bfh, ISNULL(a.fjh,'NA') +'病房' as bfmc, ISNULL(b.RYCW,'NA') as bch, ISNULL(convert (varchar,(YEAR(GETDATE())-YEAR(convert(datetime, b.CSRQ)))) ,'0') as nls, datediff(month,b.CSRQ,substring(convert(char(8),getdate(),112),1,8)) %12 as nly, b.HZXM as hzxm, convert(datetime,rtrim(ltrim(b.CSRQ))) AS birth, b.LXDH as lxdh, ISNULL(b.HYZK,'0') as hyzkdm, ( SELECT CASE b.HYZK WHEN '0' THEN '未婚' WHEN '1' THEN '已婚' WHEN '2' THEN '离独' WHEN '3' THEN '丧偶' ELSE '未知 ' END ) AS hyzk, isnull(b.MZDM,'NA') as mzdm, isnull(d.NAME,'NA') as mz, 'NA' as hkszd, 'NA' as csdz, 'NA' as gzdw, 'NA' as gzdwdz, 'NA' as gzdwdh, 'NA' as jgd, CONVERT(VARCHAR,CASE WHEN b.SFZH = '' THEN 'NA' ELSE b.SFZH END) AS sfzhm, b.BRXB as xbdm, ( SELECT CASE b.BRXB WHEN '2' THEN '女' WHEN '1' THEN '男' ELSE '其它' END ) AS xbmc,
t.SYXH as syxh, t.TJZT as tjzt, t.CJSJ as jlrq, t.FSSJ as qmrq, GETDATE() as gxsj, t.YXJL as yxjl FROM EMR_QTBLJLK t LEFT JOIN CPOE_BRSYK c ON t.SYXH = c.EMRXH LEFT JOIN EMR_BRSYK b ON b.SYXH = t.SYXH LEFT JOIN PUB_MZDMK d on b.MZDM = d.ID LEFT JOIN ${map.hisName}.dbo.ZY_BCDMK a ON a.id = b.RYCW and a.bqdm = b.RYBQ
AND t.QTBLJLXH = #{yjlxh , jdbcType= VARCHAR}

SELECT A.SYXH AS yjlxh, A.SYXH AS jzlsh, A.PATID AS patid, A.HZXM AS hzxm, case when A.MZH = '' then 'NA' else ISNULL(A.MZH,'NA') end AS mjzh, B.ZYHM AS zyh, '2' AS jzlb, A.SEX AS xbmc, CASE A.SEX WHEN '男' THEN '1' WHEN '女' THEN '2' ELSE '3' END AS xbdm, CASE WHEN A.SFZH = '' THEN 'NA' ELSE A.SFZH END AS sfzhm, A.YBDM AS jkkh, ISNULL(C.XXDM,'NA') AS aboxxdm, ISNULL(E.NAME,'NA') AS aboxxmc, ISNULL(C.RHJY,'NA') AS rhxxdm, ISNULL(F.NAME,'NA') AS rhxxmc, ISNULL(H.jbs ,'NA') AS jbs, ISNULL(H.crbs ,'NA') AS crbs, ISNULL(H.yfjzs,'NA') AS yfjzs, ISNULL(H.sss ,'NA') AS sss, ISNULL(H.sxs ,'NA') AS sxs, ISNULL(H.gms ,'NA') AS gms, ISNULL(H.grs ,'NA') AS grs, ISNULL(H.hys ,'NA') AS hys, ISNULL(H.yjs ,'NA') AS yjs, ISNULL(H.jzs ,'NA') AS jzs, GETDATE() AS gxsj, ISNULL(CONVERT(varchar,A.SYXH),'NA') AS syxh FROM CPOE_BRSYK A LEFT JOIN EMR_BRSYK B ON A.EMRXH = B.SYXH LEFT JOIN EMR_BASYK C ON C.SYXH = B.SYXH LEFT JOIN EMR_SYS_ZDFLMXK E ON C.XXDM = E.MXDM AND E.LBDM = '96' LEFT JOIN EMR_SYS_ZDFLMXK F ON C.RHJY = F.MXDM AND F.LBDM = '97' LEFT JOIN EMR_QTBLJLK G ON G.SYXH = A.EMRXH AND G.MXFLDM LIKE 'B-1020%' AND G.YXJL = 1 LEFT JOIN CIS_HLHT..HLHT_RYJL_JBXX H ON G.QTBLJLXH = H.yjlxh
and CONVERT(date,substring(A.RYRQ,1,4)+'-'+substring(A.RYRQ,5,2)+'-'+substring(A.RYRQ,7,2)) >= #{map.startDate}
and CONVERT(date,substring(A.RYRQ,1,4)+'-'+substring(A.RYRQ,5,2)+'-'+substring(A.RYRQ,7,2)) <= #{map.endDate}

SELECT CONVERT(VARCHAR,A.GHXH ) AS yjlxh, CONVERT(VARCHAR,A.GHXH ) AS jzlsh, CONVERT(VARCHAR,A.PATID) AS patid, CONVERT(VARCHAR,A.HZXM ) AS hzxm, CONVERT(VARCHAR,A.GHXH ) AS mjzh, CONVERT(VARCHAR,'NA' ) AS zyh, CONVERT(VARCHAR,'1' ) AS jzlb, CONVERT(VARCHAR,A.SEX ) AS xbmc, CONVERT(VARCHAR,CASE A.SEX WHEN '男' THEN '1' WHEN '女' THEN '2' ELSE '3' END) AS xbdm, CONVERT(VARCHAR,CASE WHEN A.SFZH = '' THEN 'NA' ELSE A.SFZH END) AS sfzhm, CONVERT(VARCHAR,A.YBDM) AS jkkh, CONVERT(VARCHAR,ISNULL(B.XXDM,'NA')) AS aboxxdm, CONVERT(VARCHAR,ISNULL(E.NAME,'NA')) AS aboxxmc, CONVERT(VARCHAR,ISNULL(B.RHJY,'NA')) AS rhxxdm, CONVERT(VARCHAR,ISNULL(F.NAME,'NA')) AS rhxxmc, CONVERT(VARCHAR,ISNULL(H.xbs,'NA') ) AS jbs, 'NA' AS crbs, 'NA' AS yfjzs, 'NA' AS sss, 'NA' AS sxs, ISNULL(H.gms,'NA') AS gms, 'NA' AS grs, 'NA' AS hys, 'NA' AS yjs, 'NA' AS jzs, GETDATE() AS gxsj, CONVERT(VARCHAR,ISNULL(A.EMRXH,'0')) AS syxh FROM OUTP_JZJLK A LEFT JOIN EMR_BASYK B ON A.EMRXH = B.SYXH LEFT JOIN EMR_SYS_ZDFLMXK E ON B.XXDM = E.MXDM AND E.LBDM = '96' LEFT JOIN EMR_SYS_ZDFLMXK F ON B.RHJY = F.MXDM AND F.LBDM = '97' LEFT JOIN EMR_QTBLJLK G ON G.SYXH = A.EMRXH AND G.MXFLDM LIKE 'B+9868%' AND G.YXJL = 1 LEFT JOIN CIS_HLHT.dbo.HLHT_MJZBL_JZLGBL H ON G.QTBLJLXH = H.yjlxh
and CONVERT(date,substring(A.GHRQ,1,4)+'-'+substring(A.GHRQ,5,2)+'-'+substring(A.GHRQ,7,2)) >= #{map.startDate}
and CONVERT(date,substring(A.GHRQ,1,4)+'-'+substring(A.GHRQ,5,2)+'-'+substring(A.GHRQ,7,2)) <= #{map.endDate}

SELECT ISNULL(CONVERT(VARCHAR,T1.XH),'NA') AS yjlxh, ISNULL(CONVERT(VARCHAR,T2.GHXH),'NA') AS jzlsh, ISNULL(CONVERT(VARCHAR,T2.PATID),'NA') AS patid, ISNULL(CONVERT(VARCHAR,T2.HZXM ),'NA') AS hzxm, ISNULL(CONVERT(VARCHAR,T2.GHXH ),'NA') AS mjzh, CASE WHEN RTRIM(LTRIM(T3.SFZH)) = '' THEN 'NA' ELSE ISNULL(CONVERT(VARCHAR,T3.SFZH ),'NA') END AS sfzhm, CASE T3.SEX WHEN '女' THEN '2' WHEN '男' THEN '1' ELSE '3' END AS xbdm, ISNULL(T3.SEX, 'NA') AS xbmc, ISNULL(CONVERT (VARCHAR,(YEAR(GETDATE())-YEAR(CONVERT(DATETIME, T3.BIRTH)))) ,'') AS nls, DATEDIFF(MONTH,T3.BIRTH,SUBSTRING(CONVERT(CHAR(8),GETDATE(),112),1,8)) %12 AS nly, ISNULL(T1.CFXH, 'NA') AS cfxh, ( SELECT t.DICT_LABEL FROM CIS_HLHT..MBZ_DICT_INFO t WHERE t.DICT_CODE = 'hospitalInfoNo' AND t.DICT_VALUE = 1) AS zzjgdm, ( SELECT t.DICT_LABEL FROM CIS_HLHT..MBZ_DICT_INFO t WHERE t.DICT_CODE = 'hospitalInfoName' AND t.DICT_VALUE = 1) AS zzjgmc, CONVERT(date,substring(T2.LRRQ,1,4)+'-'+substring(T2.LRRQ,5,2)+'-'+substring(T2.LRRQ,7,2)) AS cfklrq, T1.TS AS cfyxts, T2.KSDM AS cfklskdm, T2.KSMC AS cfklks, (SELECT T7.ZDDM FROM OUTP_MZBLZDK T7 WHERE T2.GHXH = T7.GHXH AND T7.ZDLB = 0 AND T7.ZDLX = 0 ) AS jbzdbm, (SELECT T8.ZDMC FROM OUTP_MZBLZDK T8 WHERE T2.GHXH = T8.GHXH AND T8.ZDLB = 0 AND T8.ZDLX = 0 ) AS jbzd, T1.YPDM AS ywdm, T1.YPMC AS ywmc, T1.YPGG AS ywgg, T1.JXDM AS ywjxdm, T1.JXMC AS ywjx, T1.YPJL AS ywsycjl, T1.JLDW AS ywsyjldw, T1.PCDM AS ywsypcdm, T1.PCMC AS ywsypc, T1.YPYF AS ywsytj, T1.YPYFMC AS ywsytjmc, T1.YPSL*T1.YPJL AS ywsyzjl, T1.FZXH AS cfypzh, T2.YSMC AS klysqm, T2.YSMC AS ysbm, 'NA' AS shyjsbm, 'NA' AS shyjsqm, CONVERT(DATE, '1990-01-01 00:00:00', 120) AS shyjsqmsj, 'NA' AS tpyjsbm, 'NA' AS tpyjsqm, CONVERT(DATE, '1990-01-01 00:00:00', 120) AS cftpsj, 'NA' AS hdyjsbm, 'NA' AS hdyjsqm, CONVERT(DATE, '1990-01-01 00:00:00', 120) AS cfhdsj, 'NA' AS fyyjsbm, 'NA' AS fyyjsqm, CONVERT(DATE, '1990-01-01 00:00:00', 120) AS cffysj, ISNULL(T2.MEMO, 'NA') AS cfbzxx, CONVERT(DECIMAL(18,2),(T1.YPSL*T1.YLSJ)/T1.YKXS) AS cfypje, GETDATE() AS gxsj FROM OUTP_ORDERITEM T1 LEFT JOIN OUTP_ORDER T2 ON T1.CFXH = T2.XH LEFT JOIN OUTP_JZJLK T3 ON T2.GHXH = T3.GHXH LEFT JOIN ${map.hisName}.dbo.YK_YPCDMLK T6 on T1.CD_IDM = T6.idm WHERE T6.yplh='01' AND T3.ZDDM IS NOT NULL AND T3.ZDDM != ''
and CONVERT(date,substring(T1.CJRQ,1,4)+'-'+substring(T1.CJRQ,5,2)+'-'+substring(T1.CJRQ,7,2)) >= #{map.startDate}
and CONVERT(date,substring(T1.CJRQ,1,4)+'-'+substring(T1.CJRQ,5,2)+'-'+substring(T1.CJRQ,7,2)) <= #{map.endDate}

SELECT ISNULL(T1.XH, 'NA') AS yjlxh, ISNULL(CONVERT (VARCHAR, T2.GHXH), 'NA') AS jzlsh, ISNULL( CONVERT (VARCHAR, T3.PATID), 'NA' ) AS patid, ISNULL( CONVERT (VARCHAR, T2.HZXM), 'NA' ) AS hzxm, ISNULL( CONVERT (VARCHAR, T2.GHXH), 'NA' ) AS mjzh, ISNULL( CONVERT (VARCHAR, T3.SFZH), 'NA' ) AS sfzhm, ( CASE T3.SEX WHEN '女' THEN '2' WHEN '男' THEN '1' ELSE '3' END ) AS xbdm, ISNULL(T3.SEX, 'NA') AS xbmc, ISNULL( CONVERT ( VARCHAR, ( YEAR (GETDATE()) - YEAR (CONVERT(DATETIME, T3.BIRTH)) ) ), '' ) AS nls, DATEDIFF( MONTH, T3.BIRTH, SUBSTRING ( CONVERT (CHAR(8), GETDATE(), 112), 1, 8 ) ) % 12 AS nly, ISNULL(T1.CFXH, 'NA') AS cfxh, ( SELECT t.DICT_LABEL FROM CIS_HLHT.dbo.MBZ_DICT_INFO t WHERE t.DICT_CODE = 'hospitalInfoNo' AND t.DICT_VALUE = 1 ) AS zzjgdm, ( SELECT t.DICT_LABEL FROM CIS_HLHT.dbo.MBZ_DICT_INFO t WHERE t.DICT_CODE = 'hospitalInfoName' AND t.DICT_VALUE = 1 ) AS zzjgmc, ( SELECT CASE T6.yplh WHEN '02' THEN '2' WHEN '03' THEN '1' ELSE 'NA' END ) AS cflb, 'NA' AS cflbmc, ( SUBSTRING (T2.LRRQ, 1, 4) + '-' + SUBSTRING (T2.LRRQ, 5, 2) + '-' + SUBSTRING (T2.LRRQ, 7, 2) + ' ' + SUBSTRING (T2.LRRQ, 9, 8) ) AS cfklrq, T1.TS AS cfyxts, T2.KSDM AS cfklksdm, T2.KSMC AS cfklks, ( SELECT T7.ZDDM FROM OUTP_MZBLZDK T7 WHERE T2.GHXH = T7.GHXH AND T7.ZDLB = 0 AND T7.ZDLX = 0 ) AS jbzdbm, ( SELECT T7.ZDMC FROM OUTP_MZBLZDK T7 WHERE T2.GHXH = T7.GHXH AND T7.ZDLB = 0 AND T7.ZDLX = 0 ) AS jbzd, ISNULL( ( SELECT T7.ZDDM FROM OUTP_MZBLZDK T7 WHERE T2.GHXH = T7.GHXH AND T7.ZDLB = 1 AND T7.ZDLX = 0 ), 'NA' ) AS zybmdm, ISNULL( ( SELECT T7.ZDMC FROM OUTP_MZBLZDK T7 WHERE T2.GHXH = T7.GHXH AND T7.ZDLB = 1 AND T7.ZDLX = 0 ), 'NA' ) AS zybm, ISNULL( ( SELECT T7.ZXDM FROM OUTP_MZBLZDK T7 WHERE T2.GHXH = T7.GHXH AND T7.ZDLB = 1 AND T7.ZDLX = 0 ), 'NA' ) AS zyzhdm, ISNULL( ( SELECT T7.ZXMC FROM OUTP_MZBLZDK T7 WHERE T2.GHXH = T7.GHXH AND T7.ZDLB = 1 AND T7.ZDLX = 0 ), 'NA' ) AS zyzh, T1.YPDM AS ywdm, T1.YPMC AS ywmc, T1.YPGG AS ywgg, T1.JXDM AS ywjxdm, T1.JXMC AS ywjx, T1.YPJL AS ywsycjl, T1.JLDW AS ywsyjldw, T1.PCDM AS ywsypcdm, T1.PCMC AS ywsypc, T1.YPYF AS ywsytj, T1.YPYFMC AS ywsytjmc, ( SELECT CASE T6.yplh WHEN '02' THEN T1.YPSL * T1.YPJL WHEN '03' THEN T1.YPSL ELSE 0 END ) AS ywsyzjl, T1.FZXH AS cfypzh, T1.YPMC AS zyypcf, 'NA' AS zyypjs, 'NA' AS zyypjzf, T1.PCDM AS zyyyff, 'NA' AS zfbm, 'NA' AS zzzf, T2.YSMC AS klysqm, T2.YSDM AS ysbm, 'NA' AS shyjsbm, 'NA' AS shyjsqm, 'NA' AS tpyjsbm, 'NA' AS tpyjsqm, 'NA' AS hdyjsbm, 'NA' AS hdyjsqm, 'NA' AS fyyjsbm, 'NA' AS fyyjsqm, ISNULL(T2.MEMO, 'NA') AS cfbzxx, CONVERT ( DECIMAL (18, 2), (T1.YPSL * T1.YLSJ) / T1.YKXS ) AS cfypje, GETDATE() AS gxsj FROM OUTP_ORDERITEM T1 LEFT JOIN OUTP_ORDER T2 ON T1.CFXH = T2.XH LEFT JOIN OUTP_JZJLK T3 ON T2.GHXH = T3.GHXH LEFT JOIN ${map.hisName}.dbo.YK_YPCDMLK T6 ON T1.CD_IDM = T6.idm WHERE T6.yplh IN ('02', '03') AND ( T3.ZDDM IS NOT NULL AND T3.ZDDM != '')
and CONVERT(date,substring(T1.CJRQ,1,4)+'-'+substring(T1.CJRQ,5,2)+'-'+substring(T1.CJRQ,7,2)) >= #{map.startDate}
and CONVERT(date,substring(T1.CJRQ,1,4)+'-'+substring(T1.CJRQ,5,2)+'-'+substring(T1.CJRQ,7,2)) <= #{map.endDate}
